                if (info.name === "TokenExpiredError") {
                    return res.status(401).json({
                        errors: {
                            status_code: 401,
                            message: 'Provided token is expired.',
                        }
                    });
                } else {
                    return res.status(401).json({
                        errors: {
                            status_code: 401,
                            message:  info.message
                        }
                    });
                }
            }
            req.user = user;
            return next();
        })(req, res, next);
    };

}
export default new passportManager();

